        @Override
        public String getScreenShots() {
            StringBuilder sb = new StringBuilder();
            try{
                JsonReader reader = new JsonReader(new StringReader(screenshots));
                reader.beginArray();
                while(reader.hasNext()){
                    reader.beginObject();
                    while(reader.hasNext()){
                        String tagName = reader.nextName();
                        if(tagName.equals("ori")){
                            sb.append(reader.nextString()).append(",");
                        }
                    }
                    reader.endObject();
                }
                reader.endArray();
            }
            catch(Exception e){
                e.printStackTrace();
            }
            return sb.toString();
        }

